import React,{useState} from "react";

const X = () =>{
  var [y,update] = useState({
    x:0,
    i:4,
    z:8,
  });
  
  const p = () =>{
    update((prev) => {
      return(
        {
          x:prev.x+1,
          
        }
      )
    })
  }
  return (
    <>
      <h1>{y.x}</h1>
      <button onClick={p}>click</button>
    </>
  )
}

export default X;
